function save_lire(str1) {
    sauvegarde = [];
    var str = str1.split(",");
    var ligne = 0;
    var ii = 0;
    while (ligne < str.length) {
        sauvegarde[ii] = {
            numetoile: 0,
            time: -1,
            nbr_words: 0,
            nbr_found: 0
        };
        sauvegarde[ii].numetoile = +str[ligne++];
        sauvegarde[ii].time = +str[ligne++];
        sauvegarde[ii].nbr_words = +str[ligne++];
        sauvegarde[ii].nbr_found = +str[ligne++];
        ii++
    }
}
